                description: "<p>camera frustum bottom plane</p>\n",
                type: "Number",
                optional: !0
              }, {
                name: "top",
                description: "<p>camera frustum top plane</p>\n",
                type: "Number",
                optional: !0
              }, {
                name: "near",
                description: "<p>camera frustum near plane</p>\n",
                type: "Number",
                optional: !0
